import unittest
import pandas as pd
import pendulum

from mockito import when

from oura.oura_api import OuraAPI
from oura.events import EventData


class TestEvents(unittest.TestCase):
    def test_collect_data(self):
        start_date = '2023-04-11'
        end_date = '2023-04-16'
        when(OuraAPI).get_data('sleep', start_date, end_date).thenReturn(self.mock_sleep())
        when(OuraAPI).get_data('heartrate', start_date, end_date).thenReturn(self.mock_hr())
        when(OuraAPI).get_data('tag', start_date, end_date).thenReturn(self.mock_tags())
        
        event_data = EventData(start_date, end_date)
        self.assertIsInstance(event_data.retrieve_oura_data('sleep'), list)
        self.assertIsInstance(event_data.retrieve_oura_data('heartrate'), list)
        self.assertIsInstance(event_data.retrieve_oura_data('tag'), list)
        
    def test_get_combined_events(self):
        start_date = '2023-04-11'
        end_date = '2023-04-16'
        when(OuraAPI).get_data('sleep', start_date, end_date).thenReturn(self.mock_sleep())
        when(OuraAPI).get_data('heartrate', start_date, end_date).thenReturn(self.mock_hr())
        when(OuraAPI).get_data('tag', start_date, end_date).thenReturn(self.mock_tags())

        event_data = EventData(start_date, end_date)
        combined_events = event_data.get_combined_events()
        self.assertIsInstance(combined_events, pd.DataFrame)
        self.assertTrue(sorted(list(combined_events['timestamp'])), list(combined_events['timestamp']))
        self.assertEqual(
            set(combined_events['data_type'].unique()),
            {'heart_rate', 'tag', 'hrv'},
        )
        self.assertTrue(min(combined_events['timestamp'])>=pendulum.parse(start_date))
        self.assertTrue(max(combined_events['timestamp'])<pendulum.parse(end_date))
        
